import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import HomeHeaderBar from "../Components/HomeHeaderBar";
import TopBar from "../Components/TopBar";
import Landing from "../Pages/Landing";
import Companies from "../Pages/Companies";
import AboutUs from "../Pages/AboutUs";

function Home() {
  const navigate = useNavigate();
  const [page, setPage] = useState(null);

  useEffect(() => {
    const token = sessionStorage.getItem("token");
    if (!token) {
      navigate("/");
    }
  }, [navigate]);

  const handlePage = (data) => {
    setPage(data); // Set the page state when a link is clicked
  };

  // Render the corresponding component based on the page state
  const renderPageComponent = () => {
    switch (page) {
      case "landing":
        return <Landing />;
      case "companies":
        return <Companies />;
      case "about":
        return <AboutUs />;
    case "jobs":
        return <Jobs/>
      default:
        return null; // Render nothing if page state is null or invalid
    }
  };

  return (
    <>
      <div className="top">
        <HomeHeaderBar />
        <TopBar handlePage={handlePage} page={page} />
      </div>

      <div className="body">
        {renderPageComponent()} {/* Render the page component */}
      </div>
    </>
  );
}

export default Home;
