import React, { useState } from "react";
import "../Styles/loginComponent.css";
import Button from 'react-bootstrap/Button';
import Instance from "../config/Instance";
import URL from '../config/apiURL';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
function LoginComponent() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const logged = () => toast("Logged in sucessfully!");
  const Failed = () => toast("Logge in Failed!");
  const handleSubmit =async()=>{
    const userData = {
      email: email, password: password
    }

    try{
      const response = await Instance.post(URL.login,userData );
   if( response.status === 200){
    notify();
   }
    }
    catch(error){
      console.error("Error loggin in:",error)

    }


    
  }
 
  return (
    <div className="MainLoginComponentContainer">
      <div className="mainContainer">
        <div className="signInText">
          <h4>Sign In</h4>
          <a href="/signup"> Create an account</a>
        </div>

        <hr className="horizontalLine" />

        <div className="InputFields">
          <label className="EmailLabel">Enter Email</label>
          <input className="EmailInput" type="email" onChange={(e) =>{
              setEmail(e.target.value);
          }}/>
          <label className="PasswordLabel">Enter Password</label>
          <input className="PasswordInput" type="password" onChange={(e) =>{
            setPassword(e.target.value)
          }} />
          
        </div>

        
        <label className="forgotPasswordLabel">Forgot Password?</label>
      <Button className="LoginButton" onClick={handleSubmit}> Login</Button>

      
      <hr className="horizontalLine" />
      <div className="footer">
      <p>New to Cuvette? <a href="/signUp" className="AnchorTag"> Create accounts</a></p>
      </div>
      
      </div>
      <ToastContainer/>
    </div>
  );
}

export default LoginComponent;
